","confusion_matrix_xgb <- table(Actual = y_test, Predicted = predictions_xgb)\n","print(\"Confusion Matrix (XGBoost):\")\n","print(confusion_matrix_xgb)\n","\n","accuracy_xgb <- sum(diag(confusion_matrix_xgb)) / sum(confusion_matrix_xgb)\n","print(paste(\"Accuracy (XGBoost):\", round(accuracy_xgb * 100, 2), \"%\"))\n","\n","importance_matrix <- xgb.importance(feature_names = colnames(X_train), model = xgb_model)\n","print(\"Feature Importance:\")\n","print(importance_matrix)\n","xgb.plot.importance(importance_matrix = importance_matrix)"]}],"metadata":{"kaggle":{"accelerator":"none","dataSources":[],"dockerImageVersionId":30749,"isGpuEnabled":false,"isInternetEnabled":true,"language":"r","sourceType":"notebook"},"kernelspec":{"display_name":"R","language":"R","name":"ir"},"language_info":{"codemirror_mode":"r","file_extension":".r","mimetype":"text/x-r-source","name":"R","pygments_lexer":"r","version":"4.4.0"},"papermill":{"default_parameters":{},"duration":8.212572,"end_time":"2025-05-25T11:45:24.083014","environment_variables":{},"exception":null,"input_path":"__notebook__.ipynb","output_path":"__notebook__.ipynb","parameters":{},"start_time":"2025-05-25T11:45:15.870442","version":"2.6.0"}},"nbformat":4,"nbformat_minor":5}